sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	function getGroupIdByName(group_name){
		var group = new GlideRecord('sys_user_group');
		group.addQuery('name', group_name);
		group.query();

		if (group.next()) {
			return group.sys_id;
		}

		return null;
	}

	function deleteGroupAssociationToUser(group_id, sys_id){
		var group_to_remove_member = new GlideRecord('sys_user_grmember');
		group_to_remove_member.addQuery('group', group_id);
		group_to_remove_member.addQuery('user', sys_id);
		group_to_remove_member.query();

		while (group_to_remove_member.next()) {
			group_to_remove_member.deleteRecord();
		}
	}


	function insertGroupById(group_id) {
		var group_members = new GlideRecord('sys_user_grmember');
		group_members.initialize();

		group_members.group = group_id;
		group_members.user = current.sys_id;

		var registerInsert = group_members.insert();

		if(!registerInsert){
			gs.error("Falha ao adicionar o usuário ao grupo.");
		}
	}

	var groupMap = {
		"intern": "SkillUP Intern",
		"monitor": "SkillUP Monitor",
		"manager": "SkillUP Manager"
	};

    var group_name = groupMap[current.position];
	var group_id = getGroupIdByName(group_name);

	if (!current.isNewRecord()){
		//no ultimo 1:1 falamos sobre atualizar as permissões no update, mas para isso precisa remover a permissão antiga

		var old_group = groupMap[previous.position];
		var group_to_remove_user_id = getGroupIdByName(old_group);
		deleteGroupAssociationToUser(group_to_remove_user_id, current.sys_id);
		gs.info("grupo nome" + old_group);
		gs.info("grupo nome" + group_to_remove_user_id);
	}

	insertGroupById(group_id);

})(current, previous);
