sys_script_include.script:
var SkillUPGetCourses = Class.create();
SkillUPGetCourses.prototype = {
    initialize: function() {
    },

getCourses: function(sourceInstance, username, password) {
        var endpoint = "/api/now/table/u_cursos";

        var restMessage = new sn_ws.RESTMessageV2();
        restMessage.setEndpoint("https://" + sourceInstance + endpoint);
        restMessage.setHttpMethod("GET");
        restMessage.setBasicAuth(username, password);
        restMessage.setRequestHeader("Accept", "application/json");

        var response = restMessage.execute();
        var httpStatus = response.getStatusCode();

        if (httpStatus === 200) {
            var responseBody = response.getBody();
            var responseJson = JSON.parse(responseBody);
            var courses = responseJson.result;

            if (courses && courses.length > 0) {
                gs.info("Cursos encontrados. Iniciando a importação...");
                this._populateCourses(courses);
            } else {
                gs.info("Nenhum curso encontrado na instância de origem.");
            }
        } else {
            gs.info("Erro ao buscar dados da Instância X. Status: " + httpStatus);
            gs.info("Mensagem de erro: " + response.getErrorMessage());
        }
    },

    _populateCourses: function(courses) {
        var grCourse = new GlideRecord("x_1805432_mock_ski_course");

        for (var i = 0; i < courses.length; i++) {
            var sourceCourse = courses[i];

            grCourse.initialize();

            grCourse.setValue("sys_id", sourceCourse.sys_id);
            grCourse.setValue("template", sourceCourse.u_template);
            grCourse.setValue("training", sourceCourse.u_treinamento);
            grCourse.setValue("duration", sourceCourse.u_duracao);
            grCourse.setValue("order", sourceCourse.u_ordem);
            grCourse.setValue("url", sourceCourse.u_url);
            grCourse.setValue("active", true);

            grCourse.insert();
            gs.info("Novo curso inserido: " + sourceCourse.u_treinamento);
        }
    },

    type: 'SkillUPGetCourses'
};
