sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var project = source.u_project;
	var task = source.task;

	if(gs.nil(project)){
		log.error("Projeto não preenchido. Linha do erro: " + source.sys_import_row);
		ignore = true;
		return;
	}

	var gr_project = new GlideRecord("x_1805432_mock_ski_project");
	gr_project.addQuery("project_name", project);
	gr_project.query();

	if(!gr_project.next()){
		gs.error("Projeto " + project  + "não localizado. Linha do erro:" + source.sys_import_row);
		ignore = true;
		return;
	}

	project_id = gr_project.getUniqueValue();
	target.project = project_id;

	if(!gs.nil(task)){
		var gr_task = new GlideRecord('x_1805432_mock_ski_project_task');
		gr_task.addQuery('task_name', task);
		gr_task.addQuery('project', project_id);
		gr_task.query();

		if(!gr_task.next()){
			gs.error("Verifique se a tarefa " + task + " existe. Caso sim, verifique se a mesma esta associada ao projeto " + project + ". Linha do erro: " + source.sys_import_row);
			ignore = true;
			return;
		}

		target.task = gr_task.getUniqueValue();
	}


})(source, map, log, target);
